
let edclick=false;document.getElementById('edit').onclick=function(){if(edclick==false){document.getElementsByClassName('dispn')[0].style.display='block';document.getElementById('txtar').value=document.getElementsByClassName('back_div')[0].innerHTML;document.getElementById('styledisp').style.display='none';edclick=true;stclick=false;}else{document.getElementsByClassName('dispn')[0].style.display='none';edclick=false;}}
document.getElementById('save').onclick=function(){document.getElementsByClassName('back_div')[0].innerHTML=document.getElementById('txtar').value;document.getElementsByClassName('dispn')[0].style.display='none';edclick=false;}
let stclick=false;document.getElementById('style').onclick=function(){if(stclick==false){if(edclick==true)document.getElementsByClassName('dispn')[0].style.display='none';document.getElementById('styledisp').style.display='block';stclick=true;edclick=false;}else{document.getElementById('styledisp').style.display='none';stclick=false;}}
for(let i=0;i<document.getElementsByName('fs').length;i++){document.getElementsByName('fs')[i].onclick=function(){document.getElementsByClassName('back_div')[0].style.fontSize=this.value;}}
document.getElementsByName('selfont')[0].onchange=function(){for(let i=0;i<this.options.length;i++){if(this.options[i].selected)document.getElementsByClassName('back_div')[0].style.fontFamily=this.options[i].value;}}
let colclick=false;let txtColorFl=false;for(let n=0;n<document.getElementsByClassName('colors').length;n++){document.getElementsByClassName('colors')[n].onclick=function(){if(txtColorFl){txtColorFl=false;document.getElementsByClassName('back_div')[0].style.color=this.style.background;}else{document.getElementsByClassName('back_div')[0].style.background=this.style.background;}
document.getElementsByClassName('colant')[0].style.display='none';colclick=false;}}
function changeColor(){if(colclick==false){colclick=true;document.getElementsByClassName('colant')[0].style.display='flex';if(event.target==document.getElementById('txtcolor')){txtColorFl=true;}}else{document.getElementsByClassName('colant')[0].style.display='none';colclick=false;}}
let bdtxtfl=false;document.getElementsByName('cb1')[0].onclick=function(){if(bdtxtfl==false){document.getElementsByClassName('back_div')[0].style.fontWeight='bold';bdtxtfl=true;}else{bdtxtfl=false;document.getElementsByClassName('back_div')[0].style.fontWeight='unset';}}
let crstxtfl=false;document.getElementsByName('cb2')[0].onclick=function(){if(crstxtfl==false){document.getElementsByClassName('back_div')[0].style.fontStyle='italic';crstxtfl=true;}else{crstxtfl=false;document.getElementsByClassName('back_div')[0].style.fontStyle='unset';}}
document.getElementById('add').onclick=function(){document.getElementById('ndsp_choice').style.display='block';document.getElementsByClassName('back_div')[0].style.display='none';document.getElementsByClassName('back_div')[2].style.display='none';document.getElementsByClassName('ed_st')[0].style.display='none';}
let f1=document.forms['table_frm'];document.getElementsByName('choice')[0].onclick=function(){f1.style.display='block';if(f2.style.display='block')f2.style.display='none';}
f1.createbtn.onclick=function(){let brtp;let brcl;for(let k=0;k<f1.sel_bordertype.options.length;k++){if(f1.sel_bordertype.options[k].selected)brtp=f1.sel_bordertype.options[k].value;}
for(let d=0;d<f1.sel_bordercolor.options.length;d++){if(f1.sel_bordercolor.options[d].selected)brcl=f1.sel_bordercolor.options[d].value;}
document.getElementById('txtar').value+=`<table>`;for(let i=0;i<f1.cht1.value;i++){document.getElementById('txtar').value+=`<tr>`;for(let j=0;j<f1.cht2.value;j++){document.getElementById('txtar').value+=`<td style="width:${f1.cht3.value}px; height:${f1.cht4.value}px; border:${f1.cht5.value}px ${brtp} ${brcl};"> `;document.getElementById('txtar').value+=`TD`;document.getElementById('txtar').value+=`</td>`;}
document.getElementById('txtar').value+=`</tr>`;}
document.getElementById('txtar').value+=`</table>`;f1.reset();document.getElementById('ndsp_choice').style.display='none';document.getElementsByClassName('back_div')[0].style.display='block';document.getElementsByClassName('back_div')[2].style.display='block';document.getElementsByClassName('ed_st')[0].style.display='flex';}
let f2=document.forms['list_frm'];document.getElementsByName('choice')[1].onclick=function(){f2.style.display='block';f1.style.display='none';}
f2.listbtn.onclick=function(){let lstst;for(let j=0;j<f2.sel_liststyle.options.length;j++){if(f2.sel_liststyle.options[j].selected)lstst=f2.sel_liststyle.options[j].value}
document.getElementById('txtar').value+=`<ul style ="list-style:${lstst};">`;for(let i=0;i<f2.lin1.value;i++){document.getElementById('txtar').value+=`<li>`;document.getElementById('txtar').value+=`item${i+1}`;document.getElementById('txtar').value+=`</li>`;}
document.getElementById('txtar').value+=`</ul>`;f2.reset();document.getElementById('ndsp_choice').style.display='none';document.getElementsByClassName('back_div')[0].style.display='block';document.getElementsByClassName('back_div')[2].style.display='block';document.getElementsByClassName('ed_st')[0].style.display='flex';}